import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  // const dummyMovies = [
  //   {
  //     id: 1,
  //     title: 'Some Dummy Movie',
  //     openingText: 'This is the opening text of the movie',
  //     releaseDate: '2021-05-18',
  //   },
  //   {
  //     id: 2,
  //     title: 'Some Dummy Movie 2',
  //     openingText: 'This is the second opening text of the movie',
  //     releaseDate: '2021-05-19',
  //   },
  // ];

  const [movies, setMovies] = useState([]);

  // adding a second state to show loading data from api or done
  const [isLoading, setIsloading] = useState(false);


  const fetchMoviesHandler = async () => {
    setIsloading(true);
    const result = await fetch("https://swapi.dev/api/films")
    const data = await result.json()
    // transform data
    const transferomData = data.results.map((movie) => {
      return {
        id: movie.episode_id,
        title: movie.title,
        openingText: movie.opening_crawl,
        releaseDate: movie.release_date,
      };
    });

    setMovies(transferomData);
    setIsloading(false);

  }

  // way 2
  // async function fetchMoviesHandler() {
  //   const result = await fetch("https://swapi.dev/api/films")
  //   const data = await result.json()
  //   // transform data
  //   const transferomData = data.results.map((movie) => {
  //     return {
  //       id: movie.episode_id,
  //       title: movie.title,
  //       openingText: movie.opening_crawl,
  //       releaseDate: movie.release_date,
  //     };
  //   });
  //   setMovies(transferomData);
  // }


  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && movies.length > 0 && <MoviesList movies={movies} /> }
        {!isloading && movies.length === 0 && <p>No movies to show</p>}
        {isLoading && <p>Loading...</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
